<?phprequire_once "CodonOpt_Controller_ViewResultSummary_Ancestor.php";class CodonOpt_Controller_ViewResultSummaryAll extends CodonOpt_Controller_ViewResultSummary_Ancestor {	//Constructor Takes in Current Job, and uses it to instantiate Jobs Controller	public function CodonOpt_Controller_ViewResultSummaryAll(		$InputJob,						//Optional Job		$IncludeOutputSequence			//Whether to include Output Sequence	) {		parent::__construct($InputJob);	//Parent calls job				//Extract Results		$ResultsSummaryArray = null;		if ($IncludeOutputSequence) {	//If including output sequence 			$ResultsSummaryArray =		//get all columns				CodonOpt_DAO_user_results::selectFastAndOutputByEncryptExampleId( $this->getCurrentJob()->getEncrypt_Id(), $this->getCurrentJob()->getExample_serial() );		} else {						//Otherwise not including output sequence			$ResultsSummaryArray =		//just get the fast columns				CodonOpt_DAO_user_results::selectFastColumnsByEncryptExampleId( $this->getCurrentJob()->getEncrypt_Id(), $this->getCurrentJob()->getExample_serial() );		}		$this->							//Parse Results			ParseResults($ResultsSummaryArray);	}}?>