<?php//This class is meant to generate the text for SVGsclass CodonOpt_SVGmaker_RadarData {	//List of Cells	private $DataList = array();	public function GetDataList() {		return $this->DataList;	}		public function CountDataPoints() {		return count($this->DataList);	}	public function AddDataPoint($InKey,$InValue) {		if ( isset($InKey) ) {			$this->DataList[$InKey] = $InValue;		}	}	public function GetDataPoint($InKey) {		return $this->DataList[$InKey];	//Return Cell at that integer	}		//Chart Title	private $ChartTitle;	public function getChartTitle() {		return $this->ChartTitle;	}		//Plot Color	private $PlotColor;	public function getPlotColor() {		return $this->PlotColor;	}		//Constructor Takes in whether it is plottable on XY axis	public function CodonOpt_SVGmaker_RadarData(		$InChartTitle,	//Title on Chart		$InPlotColor		//Color of Plot	) {		$this->ChartTitle = $InChartTitle;		$this->PlotColor = $InPlotColor;	}}?>