<?php//This class is meant to generate the text for SVGsclass CodonOpt_ViewResultSummary_Column {	//List of Cells	private $DataList = array();	public function CountDataPoints() {		return count($this->DataList);	}	public function AddDataPoint($input) {		if ( isset($input) ) {			array_push($this->DataList,$input);		}	}	public function GetDataPoint($CellNumber) {		$TempInt = intval($CellNumber);		//Convert to integer		return $this->DataList[$TempInt];	//Return Cell at that integer	}		//Header for Table	private $TableHeaderTitle;	public function getTableHeaderTitle() {		return $this->TableHeaderTitle;	}			//Additional CSV Title	private $CSVHeaderTitle;	public function getCSVHeaderTitle() {		return $this->CSVHeaderTitle;	}		//Chart Title	private $ChartTitle;	public function getChartTitle() {		return $this->ChartTitle;	}	//Whether this column should be plottable on XY axis	private $Plot_XY_Axis;	public function getPlot_XY_Axis() {		return $this->Plot_XY_Axis;	}		//Constructor Takes in whether it is plottable on XY axis	public function CodonOpt_ViewResultSummary_Column(		$TableHeaderTitle,		//Table Display Title (with line breaks)		$CSVHeaderTitle,		//Title for CSV		$ChartTitle,			//Title on Chart		$Plot_XY_Axis=true		//whether this should be plottable on XY axis	) {		$this->TableHeaderTitle = $TableHeaderTitle;		$this->CSVHeaderTitle = $CSVHeaderTitle;		$this->ChartTitle = $ChartTitle;		$this->Plot_XY_Axis = $Plot_XY_Axis;	}}?>