<?phprequire_once "Controllers/CodonOpt_Controller_Ancestor_User_Job.php";//Check that key variables are defined:if (	(! isset($myController) ) ||	//controller	(! isset($myPage) )				//and Form submission page) {									//If not defined redirect back to submit new job	header( "Location: ".CodonOpt_Controller_Ancestor_User_Job::getSubmitNewJobPage() );		exit;} //Extract form data and feed it to the controller$posted = false;foreach($_POST as $tempKey => $tempValue) {	switch ($tempKey) {		case "is_input_protein":			$myController->setIs_input_protein($tempValue);			break;		case "nucleotide_input_translation_rule":			$myController->setNucleotide_input_translation_rule($tempValue);			break;		case "InputSequence":			$myController->setInputSequence($tempValue);			break;		case "Title":			$myController->setTitle($tempValue);			break;		case "posted":	//Hidden input			$posted = true;			break;	}}if ($posted) {			//If hidden input found: Check and Run	$myController->checkAndSave();}?>